mapper.select:
SELECT ac.name 'name', ac.list_image_url 'listImageUrl', ac.detail_image_url 'detailImageUrl', ac.activity_link_url 'activityLinkUrl', ac.activity_type 'activityType', b.logo 'logo', b.company 'company', ac.activity_type as 'activityType', ac.introduction as 'introduction', ac.id as 'id', ac.end_date as 'endDate', b.deal_type as 'dealType', IFNULL(b.max_user_rebate, 0) as 'maxUserRebate', b.discount as 'discount', b.url as 'url', b.id as 'businessId', b.business_introduce as 'businessIntroduce' FROM ad_coupon_activity ac INNER JOIN ad_group_activity_conn cn ON ac.id = cn.activity_id INNER JOIN ad_business b ON ac.business_id = b.id INNER JOIN ad_activity_category acc ON ac.category_id = acc.id WHERE ac.id = cn.activity_id AND cn.group_id = #{groupId} AND ac.plateform_type in (#{client},99) AND ac.delete_flag = '0' AND ac.state = '1' AND (now() between ac.begin_date and ac.end_date)
AND acc.id = #{categoryId}
AND ac.is_recommendation = #{isRecommendation}
ORDER BY ac.sort DESC LIMIT #{startIndex},#{pageSize}

SELECT count(1) FROM ad_coupon_activity ac INNER JOIN ad_group_activity_conn cn ON ac.id = cn.activity_id INNER JOIN ad_business b ON ac.business_id = b.id INNER JOIN ad_activity_category acc ON ac.category_id = acc.id WHERE ac.id = cn.activity_id AND cn.group_id = #{groupId} AND ac.plateform_type in (#{client},99) AND ac.delete_flag = '0' AND ac.state = '1' AND (now() between ac.begin_date and ac.end_date)
AND acc.id = #{categoryId}
AND ac.is_recommendation = #{isRecommendation}
