mapper.select:
SELECT c.product_name AS "productName", c.description AS "description", c.business_online_url AS "businessOnlineUrl", c.begin_date AS "beginDate", c.end_date AS "endDate", c.kind AS "kind", c.coupon_value AS "couponValue", c.remarks AS "remarks", c.coupon_user_detail AS "couponUserDetail", c.coupon_category AS 'couponCategory', c.description AS 'description', b.logo AS "wechatDetailImage" FROM ad_coupon c INNER JOIN ad_business b ON c.business_id = b.id WHERE NOW() between c.begin_date AND c.end_date AND c.del_flag = 0 AND c.is_open = 1 AND c.receive_channel = 1
AND b.id = #{businessId}
AND c.category_type = #{categoryType}
LIMIT #{startIndex},#{pageSize}

mapper.update:
update 
	ad_coupon a 
set 
	a.distribution_quantity = distribution_quantity - #{count},
	a.remind_quantity = a.remind_quantity + #{count} 
where a.id = #{couponId}
