mapper.select:
		SELECT
            SUM(IF(a.flow_type=0,a.integral_rebate_amount,-a.integral_rebate_amount)) AS "totalServiceCharge"
		FROM
		    ad_order_integral_record a
		WHERE
		      a.status = 0

                AND a.order_number = #{orderNumber}


                AND a.type = #{type}


                AND a.payment_type = #{paymentType}
