SELECT d.logo as 'logo', d.company as 'company', a.order_id, a.amount, a.create_date, c.store_name as "storeName", a.type, a.status FROM ad_return_points a LEFT JOIN _order b ON a.order_id = b.id LEFT JOIN ad_business d ON d.business_id = b.bussinessId LEFT JOIN ad_business_store c ON b.storesId = c.store_number AND d.id = c.business_id WHERE a.create_date between #{beginDate} and #{endDate} and a.status = 2 and a.user_id IN
#{userId}
ORDER BY a.create_date DESC

