mapper.select:
SELECT count(DISTINCT id)
FROM _order o
WHERE o.userid = #{userId} AND type = 1 AND state = 1;

select 
	 
from 
_order a 
where a.userid=#{userId} and a.totalAmount != 0 and orderDate < #{beginDate} 
and type =1 and state = 1
ORDER BY a.orderDate desc limit 1;

select 
	 
from 
_order a 
where a.userid=#{userId} and orderDate > #{beginDate} and orderDate < #{endDate} 
and a.totalAmount != 0 and type =1 and state = 1
ORDER BY a.orderDate desc;

